import re, requests
def GetLSProData(page_data,Cookie_Jar,m):
	headers = {'User-Agent': 'Mozilla/5.0 (X11; Ubuntu; Linux x86_64; rv:50.0) Gecko/20100101 Firefox/50.0'}
	data = []
	sites = ['http://www.bajuineros.com/pelicula/hotel-transilvania-3-unas-vacaciones-monstruosas/', 'https://www.tupelihd.com/hotel-transilvania-3-unas-vacaciones-monstruosas/', 'https://www.peliculastoday.com/hotel-transilvania-3-unas-vacaciones-monstruosas/', 'http://www.cuevana3.com/5470/ver-hotel-transylvania-3-summer-vacation-online', 'https://www.cinetux.com/7558/hotel-transilvania-2.html']
	
	for site in sites:
		source = requests.get(site, headers=headers).text
		data += re.findall('"(https://streamango.*?|http://xvidstage.*?|https://openload.*?|https://userscloud.*?|https://ok.*?|https://uptostream.*?|https://vidoza.*?)"', source)
	return data
